package gocipher

import (
	"fmt"
	"testing"
)

func TestVigenere(t *testing.T) {
	text := ""
	key, err := VigenereCrack(text, 8)
	if err != nil {
		t.Fatal(err)
	}
	v := NewVigenere(key)
	plain := v.Decrypt(text)
	fmt.Printf("key: %v\nplaintext: %s\n", key, plain)
	cipher := v.Encrypt(plain)
	if cipher != text {
		t.Errorf("roundtrip not equal: %s", cipher)
	}
}
